package com.eg;

import java.util.regex.Matcher;
import java.util.regex.Pattern;

/**
 * @Name: MatchPage
 * @Description: 模拟网页，获取指定内容
 * @User: xdSun
 * @Date: 2023/09/02 21:20:44
 * @Version: 1.0
 **/
public class MatchPage {
    public static void main(String[] args) {
        // simple
        test1();
        // complex
        test2();
    }

    /**
     * complex example
     */
    private static void test2() {
        System.out.println("\n匹配标题=============>");
        // 匹配标题
        matchAllTitle();
    }

    /**
     * 匹配标题
     */
    private static void matchAllTitle() {
        // title="波兰禁演柴可夫斯基和契诃夫作品"
        String regex = "title=\"(\\S+)\"";
        Pattern compile = Pattern.compile(regex);
        Matcher matcher = compile.matcher(Content.CONTENT_COMPLEX);
        while (matcher.find()) {
            System.out.println(matcher.group(0) + "\tmatcher.grout(1) : " + matcher.group(1));
        }
    }

    /**
     * simple example
     */
    private static void test1() {
        System.out.println("\n匹配所有的英文单词=======>");
        // 匹配字符串中的所有英文单词
        matchAllWord();
        System.out.println("\n匹配所有的数字==========>");
        // 匹配字符串中的所有数字
        matchAllDigit();
        System.out.println("\n匹配所有的单词或者数字==========>");
        // 匹配所有的单词或者数字
        matchAllStringOrDigit();
    }

    /**
     * 匹配所有的单词或者数字
     */
    private static void matchAllStringOrDigit() {
        String regex = "\\b[\\p{Alpha}]{2,}\\b|[\\p{Digit}]+";
        Pattern compile = Pattern.compile(regex);
        Matcher matcher = compile.matcher(Content.CONTENT_SIMPLE);
        while (matcher.find()) {
            System.out.print(matcher.group(0) + "\t");
        }
    }

    /**
     * 匹配字符串中的所有数字
     */
    private static void matchAllDigit() {
        String regex = "\\d+";
//         String regex = "[\\p{Digit}]+";
        Pattern compile = Pattern.compile(regex);
        Matcher matcher = compile.matcher(Content.CONTENT_SIMPLE);
        while (matcher.find()) {
            System.out.print(matcher.group(0) + "\t");
        }
    }

    /**
     * 匹配字符串中的所有英文单词
     */
    private static void matchAllWord() {
        String regex = "\\b[\\p{Alpha}]{2,}\\b";
//        String regex = "\\b[a-zA-Z]{2,}\\b";
        Pattern compile = Pattern.compile(regex);
        Matcher matcher = compile.matcher(Content.CONTENT_SIMPLE);
        while (matcher.find()) {
            System.out.print(matcher.group(0) + "\t");
        }
    }

}

